INSERT INTO `events` (`id`, `title`, `year`, `short_description`, `full_description`, `images`, `link`, `metadata`, `place_id`) VALUES
(1, 'Первый удар немецко-фашистских захватчиков 22 июня 1941', 1941, 'Первый удар немецко-фашистских захватчиков принял 86-й Августовский пограничный отряд', '  Первыми в борьбу с наступавшим противником на подступах к Гродно вступили воины 86-го Августовского пограничного отряда (начальник майор Г.К. Здорный). Насмерть стояли на своих рубежах пограничники. Враг обходил, окружал и уничтожал пограничные заставы, но не смог заставить сдаться ни одну из них.\n   Героически сражалась 3-ья застава отряда под командованием лейтенанта Усова. В течении 8-ми часов пограничники отбивали атаки фашистов. Из 32 защитников заставы только 5 прорвалось к своим, после приказа отходить.', 'images\\events_border\\AugostChanel.png', 'states/s2.html', '0;480 Первый удар немецко-фашистских захватчиков 22 июня 1941', 1),
(2, 'Операция "Багратион" 13 июля 1944', 1944, 'В ходе этой операции Гродно был освобожден от немецко-фашистских оккупантов.', '', '', NULL, '200;300 Операция "Багратион" 13 июля 1944', 1),
(3, 'Выселение евреев в Гродно 1 октября 1941', 1941, '1 октября 1941 года всех евреев Гродно выселили в 2 гетто.', 'Всех евреев Гродно выселили в 2 гетто. Одно из них располагалось в районе современных улиц Замковая - Троицкая - Виленская _ советская, второе - Лидская - Белуша - Антонова - проспект Космонавтов. Евреи должны были принудительно выполнять тяжелые работы, многие их них погиабли от нечеловечких условий жизни и работы. Ужасные зверства и издевательства синили гестаповцы Курт Визэ(начальник гетто №1), Отто Стребле (начальник гетто №2), Бест, Райцлер, Шотт, Шадель, Раме и др. ', '', NULL, 'Выселение евреев в Гродно 1 октября 1941', 1),
(5, 'Ликвидирование Гродненских гетто 5 марта 1943', 1943, 'В марте 1943 года оба гетто были ликвидированы.', 'В марте 1943 года оба гетто были ликвидированы. Как свидетельствует отчет гродненского уездного комиссара фон Плетца от 5 марта 1943 г. шефу гражданского управления Белостокского округа, в лагерь уничтожения было вывезено 20577 евреев. Оттуда они не вернулись.', '', NULL, 'Ликвидирование Гродненских гетто 5 марта 1943', 1),
(6, 'Ликвидирование Гродненских гетто 1 ноября 1942', 1942, 'С 1 ноября 1942 г. гитлеровцы начали насильственный вывоз евреев из большинства поселков не только Гродненского уезда', 'По сведениям узников гетто, которые остались живыми,и архивных документах, с 1 ноября 1942 г. гитлеровцы начали насильственный вывоз евреев из большинства поселков не только Гродненского уезда, но и других уездов Белостоцкого округа. Их отправляли в лагеря смерти Треблинку и Освенцим. Фашисты хотели как можно скорее ликвидировать многочисленные гетто, на охрану которых требовались большие силы полиции. А они нужны были для других целей - для участия в карательных операциях, отправки на фронт.', '', 'states/s2.html', 'С 1 ноября 1942 г. гитлеровцы начали насильственный вывоз евреев из большинства поселков не только Гродненского уезда', 1),
(7, 'В середине декабря 1942 года узников Колбасино начали направлять в лагеря смерти', 1942, 'В середине декабря 1942 года узников Колбасино начали направлять в лагеря смерти.', 'В середине декабря 1942 года узников Колбасино начали направлять в лагеря смерти. Гитлеровцы разработали четкую процедуру своей акции. Ночью в условиях бури и сильного мороза их пешком погнали на железнодорожную станцию Лососно.При этом Рынтцлер заставлял петь еврейскую песню  "Iдл мiтн фiлд"(еврей со скрипкой). Старых и больных, отставали, расстреливали. На станции заключенных заталкивали в битком набитые в товарные вагоны, и поезда отвозили в последний путь ни в чем не виноватых людей. В Треблинк транспорт прибывал на последний день, в Освенцим - через 2-3 дня. А там собрания жертв отправляли в крематорий.\r\nЗа неделю лагерь Колбасино почти опустел. Оставшихся, около 3 тыс. человек по приказу гестапо вернули в Гродно. Окровавленные, обмороженные, сбитые люди возвращались в гетто №1.Тут им стремились облегчить муки, но многие из них через несколько дней умирали.\r\nПреступления нацистов в отношении заключении гродненских гетто заставили многих искать выход из такого положения. Встал вопрос о создании сопротивления фашистам.', '', 'states/s2.html', 'В середине декабря 1942 года узников Колбасино начали направлять в лагеря смерти.', 1),
(8, 'В январе 1942 года в гетто № 1 была создана подпольная антифашистская организация', 1943, 'В январе 1942 года в гетто № 1 была создана подпольная антифашистская организация', 'В январе 1942 года в гетто № 1 была создана подпольная антифашистская организация, в которую входили коммунисты И. Хмельник, Л. Липский, И. Рейзнер и др. Подпольщики в тайне слушали советское радио, радиоприемник находился в подвале дома на углу улицы Кошевого и Найдуса. Затем они распространяли среди населения написанные от руки или напечатанные на пишущей машинке сводки Совинформбюро. Организация оказывала содействие военнослужащим и людям, которых преследовало гестаппо, помогало им скрываться и выходить из города, подделывали документы для подпольщиков, доставляло оружие, компасы и карты. Одним из организаторов групп молодежи для выхода их гетто, был учитель С. А. Жуковский. ', '', NULL, 'В январе 1942 года в гетто № 1 была создана подпольная антифашистская организация', 1),
(9, 'В январе 1943 г. группе еврейской молодежи из 18 человек удалось вырваться из гетто №1.', 1943, 'В январе 1943 г. в дни акции нацистов по отправке евреев из гетто №1 в лагерь смерти группе еврейской молодежи из 18 человек под управлением И.Т. Панкуса удалось вырваться.', 'В январе 1943 г. в дни акции нацистов по отправке евреев из гетто №1 в лагерь смерти группе еврейской молодежи из 18 человек под управлением И.Т. Панкуса удалось вырваться, и они направились в Августовские леса. По дороге они купили у местных деревенских жителей несколько винтовок. Однако один их жителей Коробчиц донес гитлеровцам, что группа осталась на ночлег в деревне, сюда прибыли фашисты и с помощью гранат и автоматов перебили всех сбежавших с гетто, в том числе, что там погибли, были М.Пупко, Г.Рубарский, Х.Мараш(имена остальных не установлены).', '', 'states/s2.html', 'В январе 1943 г. группе еврейской молодежи из 18 человек удалось вырваться из гетто №1. Коробчицы', 1),
(10, 'Освобождение 3 июля 1944 г. Островецкого  района', 1944, '3 июля 1944 г. Островецкий  район: В освобождении р-на участвовала партизанская бригада имени ЦК КП(б)Б (командир  М.И.Федоров), которая взяла и удерживала р-н до 6.7.1944 г., когда подошли войска 3-го Белорусского фронта.', '3 июля 1944 г. Островецкий  район: В освобождении р-на участвовала партизанская бригада имени ЦК КП(б)Б (командир  М.И.Федоров), которая взяла и удерживала р-н до 6.7.1944 г., когда подошли войска 3-го Белорусского фронта.', '', 'states/s2.html', '3 июля 1944 г. Островецкий  район: В освобождении р-на участвовала партизанская бригада имени ЦК КП(б)Б (командир  М.И.Федоров), которая взяла и удерживала р-н до 6.7.1944 г., когда подошли войска 3-го Белорусского фронта.', 10),
(11, 'Освобождение 5 июля 1944 г. Сморгонского  района', 1944, '5 июля 1944 г. Сморгонский  район: В ходе Минской операции от немецко-фашистских оккупантов освобожден город Сморгонь. В освобождении р-на участвовали 35-я гвардейская танковая бригада (генерал-майор А.А. Асланов), 7-я гвардейская механизированная бригада', '5 июля 1944 г. Сморгонский  район: В ходе Минской операции от немецко-фашистских оккупантов освобожден город Сморгонь. В освобождении р-на участвовали 35-я гвардейская танковая бригада (генерал-майор А.А. Асланов), 7-я гвардейская механизированная бригада (полковник М.И. Родионов); партизанская бригада «Народные мстители» имени В.Т. Воронянского (командир  Г.Ф.Покровский).', '', 'states/s2.html', 'Освобождение 5 июля 1944 г. Сморгонского  района', 11),
(12, 'Освобождение 7 июля 1944 г. Ошмянского район', 1944, '7 июля 1944 г. Ошмянский район: В ходе Вильнюсской операции войсками 3-го Белорусского фронта освобожден г. Ошмяны. В освобождении р-на участвовали 3-я (подполковник К.А. Гриценко) и 19-я (полковник Г.А. Походеев) гвардейские танковые бригады; 2-я гвардей', '7 июля 1944 г. Ошмянский район: В ходе Вильнюсской операции войсками 3-го Белорусского фронта освобожден г. Ошмяны. В освобождении р-на участвовали 3-я (подполковник К.А. Гриценко) и 19-я (полковник Г.А. Походеев) гвардейские танковые бригады; 2-я гвардейская мотострелковая бригада (подполковник К.А. Андронов) 3-го гвардейского танкового корпуса 5-й гвардейской танковой армии 3-го Белорусского фронта.', '', 'states/s2.html', '7 июля 1944 г. Ошмянский район: В ходе Вильнюсской операции войсками 3-го Белорусского фронта освобожден г. Ошмяны. В освобождении р-на участвовали 3-я (подполковник К.А. Гриценко) и 19-я (полковник Г.А. Походеев) гвардейские танковые бригады; 2-я гвардей', 12),
(13, 'Освобождение 7 июля 1944 г. Юратишковского района', 1944, '7 июля 1944 г. Юратишковский район: (центр – г.п.Юратишки, ныне в Ивьевском р-не). В освобождении р-на участвовала 32-я кавалерийская дивизия (генерал-майор И.П. Калюжный) 3-го гвардейского кавалерийского корпуса 3-го Белорусского фронта.', '7 июля 1944 г. Юратишковский район: (центр – г.п.Юратишки, ныне в Ивьевском р-не). В освобождении р-на участвовала 32-я кавалерийская дивизия (генерал-майор И.П. Калюжный) 3-го гвардейского кавалерийского корпуса 3-го Белорусского фронта.', '', 'states/s2.html', '7 июля 1944 г. Юратишковский район: (центр – г.п.Юратишки, ныне в Ивьевском р-не). В освобождении р-на участвовала 32-я кавалерийская дивизия (генерал-майор И.П. Калюжный) 3-го гвардейского кавалерийского корпуса 3-го Белорусского фронта.', 16),
(14, 'Освобождение 13 июля 1944 г. Мостовского района', 1944, ' В освобождении р-на участвовала 348-я стрелковая дивизия (генерал-майор М.А. Никитин) 35-го стрелкового корпуса  3-й армии 2-го Белорусского фронта.', 'В освобождении р-на участвовала 348-я стрелковая дивизия (генерал-майор М.А. Никитин) 35-го стрелкового корпуса  3-й армии 2-го Белорусского фронта.', '', 'states/s2.html', 'Освобождение 13 июля 1944 г. Мостовского района', 20),
(15, 'Освобождение 14 июля 1944 г. Волковысского района', 1944, 'Освобождение 14 июля 1944 г. Волковысского района. В ходе Белостокской операции войсками 2-го Белорусского фронта освобожден г. Волковыск.', ' В ходе Белостокской операции войсками 2-го Белорусского фронта освобожден г. Волковыск. 11 частям и соединениям, отличившимся при освобождении города, присвоены почетные наименования “Волковысских”. В освобождении р-на участвовали 129-я (генерал-майор И.В. Панчук), 169-я (полковник Ф.А. Веревкин), 5-я (полковник П.Т. Михалицын) стрелковые дивизии 40-го стрелкового корпуса, 1880-й самоходный артиллерийский полк (майор П.И. Ведехин); 44-я отдельная истребительно-противотанковая артиллерийская бригада (полковник М.Г. Сыроваткин), 28-я зенитно-артиллерийская дивизия (полковник Г.Я. Дробков) 3-й армии; авиадивизия 4-й воздушной армии 2-го Белорусского фронта.', '', 'states/s2.html', ' В ходе Белостокской операции войсками 2-го Белорусского фронта освобожден г. Волковыск. 11 частям и соединениям, отличившимся при освобождении города, присвоены почетные наименования “Волковысских”. В освобождении р-на участвовали 129-я (генерал-майор И.', 21),
(16, 'Освобождение 17 июля 1944 г. Свислочского района', 1944, '17 июля 1944 г. Свислочский район: В освобождении р-на участвовала 82-я стрелковая дивизия (генерал-майор И.В. Писарев).', '17 июля 1944 г. Свислочский район: В освобождении р-на участвовала 82-я стрелковая дивизия (генерал-майор И.В. Писарев).', '', 'states/s2.html', '17 июля 1944 г. Свислочский район: В освобождении р-на участвовала 82-я стрелковая дивизия (генерал-майор И.В. Писарев).', 22),
